import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import '../App.css';
import gql from 'graphql-tag';
import { Query, Mutation } from 'react-apollo';

const GET_BOOK = gql`
    query book($bookId: String) {
        book(id: $bookId) {
            _id
            isbn
            title
            author
            description
            published_year
            publisher
            updated_date
        }
    }
`;

const DELETE_BOOK = gql`
  mutation removeBook($id: String!) {
    removeBook(id:$id) {
      _id
    }
  }
`;

class Show extends Component {
  render() {
    return (
      <Query pollInterval={500} query={GET_BOOK} variables={{ bookId: this.props.match.params.id }}>
        {({ loading, error, data }) => {
          if (loading) return 'Loading...';
          if (error) return `Error! ${error.message}`;

          return (
            <div className="container">
              <div className="panel panel-default">
                <div className="panel-heading">
                  <h4><Link to="/">Book List</Link></h4>
                  <h3 className="panel-title">
                    {data.book.title}
                  </h3>
                </div>
                <div className="panel-body">
                  <dl>
                    <dt>ISBN:</dt>
                    <dd>{data.book.isbn}</dd>
                    <dt>Author:</dt>
                    <dd>{data.book.author}</dd>
                    <dt>Description:</dt>
                    <dd>{data.book.description}</dd>
                    <dt>Published Year:</dt>
                    <dd>{data.book.published_year}</dd>
                    <dt>Publisher:</dt>
                    <dd>{data.book.publisher}</dd>
                    <dt>Updated:</dt>
                    <dd>{data.book.updated_date}</dd>
                  </dl>
                  <Mutation mutation={DELETE_BOOK} key={data.book._id} onCompleted={() => this.props.history.push('/')}>
                    {(removeBook, { loading, error }) => (
                      <div>
                        <form
                          onSubmit={e => {
                            e.preventDefault();
                            removeBook({ variables: { id: data.book._id } });
                          }}>
                          <Link to={`/edit/${data.book._id}`} className="btn btn-success">Edit</Link>
                          {' '}
                          <button type="submit" className="btn btn-danger">Delete</button>
                        </form>
                        {loading && <p>Loading...</p>}
                        {error && <p>Error :( Please try again</p>}
                      </div>
                    )}
                  </Mutation>
                </div>
              </div>
            </div>
          );
        }}
      </Query>
    );
  }
}

export default Show;
